function addRecipeToUserFavorites(){const n=document.querySelector(".favorites"),t=n.querySelector("i");t.addEventListener("click",()=>{const n=document.querySelector(".favorites span"),t=document.getElementById("recipe-id").value,i=document.querySelector("#antiForgeryForm input[name=__RequestVerificationToken]").value;let r={recipeId:t};const u={method:"post",headers:{"Content-Type":"application/json","X-CSRF-TOKEN":i},body:JSON.stringify(r)},f=await fetch("/api/Favorites",u),e=await f.json();n.textContent=e})}window.onload=addRecipeToUserFavorites();