const AddIngredient=function(){let t=document.getElementById("addIngredient"),n=document.getElementById("IngredientsContainer");t.onclick=function(t){t.preventDefault();let i=n.getElementsByTagName("input").length;AddIngredientAndQuantity(n,i)}},AddIngredientAndQuantity=function(n,t){let u=Math.ceil(t/2),f=CreateDiv();f.setAttribute("class","row mb-3");let e=CreateDiv();e.setAttribute("class","col");let i=CreateInput();i.setAttribute("name","Ingredients["+u+"].IngredientName");i.setAttribute("class","form-control");i.setAttribute("placeholder","Съставка");let o=CreateSpan();o.setAttribute("class","text-danger");o.setAttribute("asp-validation-for","Ingredients["+u+"].IngredientName");i.appendChild(o);e.appendChild(i);let s=CreateDiv();s.setAttribute("class","col");let r=CreateInput();r.setAttribute("name","Ingredients["+u+"].Quantity");r.setAttribute("class","form-control");r.setAttribute("placeholder","Количество");let h=CreateSpan();h.setAttribute("class","text-danger");h.setAttribute("asp-validation-for","Ingredients["+u+"].Quantity");r.appendChild(h);s.appendChild(r);f.appendChild(e);f.appendChild(s);n.appendChild(f)},CreateDiv=function(){return document.createElement("div")},CreateInput=function(){return document.createElement("input")},CreateLabel=function(){return document.createElement("label")},CreateSpan=function(){return document.createElement("span")};window.onload=AddIngredient();