function addIngredient(){let t=document.getElementById("addIngredient"),n=document.getElementById("IngredientsContainer");t.onclick=function(t){t.preventDefault();let i=n.getElementsByTagName("input").length;addIngredientAndQuantity(n,i)}}function addIngredientAndQuantity(n,t){let u=Math.ceil(t/2),f=createDiv();f.setAttribute("class","row mb-3");let e=createDiv();e.setAttribute("class","col");let i=createInput();i.setAttribute("name","Ingredients["+u+"].IngredientName");i.setAttribute("class","form-control");i.setAttribute("placeholder","Съставка");let o=createSpan();o.setAttribute("class","text-danger");o.setAttribute("asp-validation-for","Ingredients["+u+"].IngredientName");i.appendChild(o);e.appendChild(i);let s=createDiv();s.setAttribute("class","col");let r=createInput();r.setAttribute("name","Ingredients["+u+"].Quantity");r.setAttribute("class","form-control");r.setAttribute("placeholder","Количество");let h=createSpan();h.setAttribute("class","text-danger");h.setAttribute("asp-validation-for","Ingredients["+u+"].Quantity");r.appendChild(h);s.appendChild(r);f.appendChild(e);f.appendChild(s);n.appendChild(f)}function createDiv(){return document.createElement("div")}function createInput(){return document.createElement("input")}function createLabel(){return document.createElement("label")}function createSpan(){return document.createElement("span")}window.onload=addIngredient();