function paintRecipeInListBorderLeft(){const n=document.querySelectorAll(".recipe-in-list-container");for(let t=0;t<n.length;t++){let i=n[t].querySelector(".recipe-in-list-color").value,r=n[t].querySelector(".recipe-in-list-container-name");r.style.borderLeft="10px solid "+i}}window.onload=paintRecipeInListBorderLeft();