const ScrollToCommentForm=function(){let n=document.getElementById("add-recipe-comment");const t=document.querySelector(".paragraph-warning");let i=document.querySelector(".wrapper-for-comments-elements");n.addEventListener("click",r=>{if(r.preventDefault(),t!=null){let t=document.createElement("h3");t.setAttribute("class","text-danger text-center");t.textContent="Само за регистрирани потребители!";i.prepend(t);n.style.display="none"}else document.getElementById("recipe-comment-form").scrollIntoView({behavior:"smooth"})})};window.onload=ScrollToCommentForm();