const ScrollToCommentForm=function(){let n=document.getElementById("add-recipe-comment");const i=document.querySelector(".paragraph-warning");let r=document.querySelector(".wrapper-for-comments-elements"),t=document.getElementById("recipe-comment-form");n.addEventListener("click",u=>{if(u.preventDefault(),i!=null){let t=document.createElement("h3");t.setAttribute("class","text-danger text-center");t.textContent="Само за регистрирани потребители!";r.prepend(t);n.style.display="none"}else t.style.display="block",t.scrollIntoView({behavior:"smooth"})})};window.onload=ScrollToCommentForm();